// src/components/EditUserForm.js
import React from 'react';
import axios from 'axios';

class EditUserForm extends React.Component {
  state = {
    name: '',
    email: '',
    age: ''
  };

  componentDidMount() {
    const userId = this.props.match.params.id;
    axios.get(`http://localhost:3000/users/${userId}`)
      .then(response => {
        const { name, email, age } = response.data;
        this.setState({ name, email, age });
      })
      .catch(error => {
        console.error('Error fetching user:', error);
      });
  }

  handleChange = e => {
    this.setState({ [e.target.name]: e.target.value });
  };

  handleSubmit = e => {
    e.preventDefault();
    const { name, email, age } = this.state;
    const userId = this.props.match.params.id;
    axios.put(`/api/users/${userId}`, { name, email, age })
      .then(response => {
        console.log('User updated successfully');
      })
      .catch(error => {
        console.error('Error updating user:', error);
      });
  };

  render() {
    const { name, email, age } = this.state;

    return (
      <div>
        <h1>Edit User</h1>
        <form onSubmit={this.handleSubmit}>
          <input type="text" name="name" value={name} onChange={this.handleChange} placeholder="Name" required />
          <input type="email" name="email" value={email} onChange={this.handleChange} placeholder="Email" required />
          <input type="number" name="age" value={age} onChange={this.handleChange} placeholder="Age" required />
          <button type="submit">Update User</button>
        </form>
      </div>
    );
  }
}

export default EditUserForm;
