import React, { useState, useEffect } from 'react';
import { Table, Input, Popconfirm, Form, Typography, message, Button, Card } from 'antd';
import { PlusOutlined } from '@ant-design/icons';
import axios from 'axios';

const { Text } = Typography;

const UserList = () => {
  const [users, setUsers] = useState([]);
  const [editingKey, setEditingKey] = useState('');
  const [form] = Form.useForm();
  const [nextId, setNextId] = useState(1); // Nuevo estado para el próximo ID

  useEffect(() => {
    fetchData();
  }, []);

  const fetchData = async () => {
    try {
      const response = await axios.get('http://localhost:3000/users');
      setUsers(response.data);
      // Establecer el próximo ID basado en el máximo ID existente
      const maxId = response.data.reduce((max, user) => (user.id > max ? user.id : max), 0);
      setNextId(maxId + 1);
    } catch (error) {
      console.error('Error fetching users:', error);
      message.error('Error fetching users');
    }
  };

  const isEditing = (record) => record.id === editingKey;

  const edit = (record) => {
    setEditingKey(record.id);
    form.setFieldsValue(record);
  };

  const cancel = () => {
    setEditingKey('');
  };

  const save = async (id) => {
    try {
      const row = await form.validateFields();
      if (id === 'new') {
        const response = await axios.post('http://localhost:3000/users', row);
        const newUser = { ...row, id: response.data.id };
        const newData = [...users, newUser];
        setUsers(newData);
        setEditingKey('');
        message.success('User added successfully');
      } else {
        const newData = [...users];
        const index = newData.findIndex((item) => id === item.id);
        if (index > -1) {
          const item = newData[index];
          newData.splice(index, 1, { ...item, ...row });
          setUsers(newData);
          setEditingKey('');
          await axios.put(`http://localhost:3000/users/${id}`, row);
          message.success('User updated successfully');
        }
      }
    } catch (error) {
      console.error('Error saving user:', error);
      message.error('Error saving user');
    }
  };

  const handleAddUser = () => {
    const newUser = { id: 'new', name: '', email: '', age: '' };
    setUsers([...users, newUser]);
    setEditingKey('new');
  };

  const columns = [
    {
      title: 'ID',
      dataIndex: 'id',
    },
    {
      title: 'Name',
      dataIndex: 'name',
      editable: true,
      render: (_, record) => {
        const editable = isEditing(record);
        return editable ? (
          <Form.Item name="name" style={{ margin: 0 }}>
            <Input />
          </Form.Item>
        ) : (
          <Text>{record.name}</Text>
        );
      },
    },
    {
      title: 'Email',
      dataIndex: 'email',
      editable: true,
      render: (_, record) => {
        const editable = isEditing(record);
        return editable ? (
          <Form.Item name="email" style={{ margin: 0 }}>
            <Input />
          </Form.Item>
        ) : (
          <Text>{record.email}</Text>
        );
      },
    },
    {
      title: 'Age',
      dataIndex: 'age',
      editable: true,
      render: (_, record) => {
        const editable = isEditing(record);
        return editable ? (
          <Form.Item name="age" style={{ margin: 0 }}>
            <Input />
          </Form.Item>
        ) : (
          <Text>{record.age}</Text>
        );
      },
    },
    {
      title: 'Action',
      dataIndex: 'action',
      render: (_, record) => {
        const editable = isEditing(record);
        return editable ? (
          <span>
            <Button type="primary" onClick={() => save(record.id)} style={{ marginRight: 8 }}>
              Save
            </Button>
            <Popconfirm title="Sure to cancel?" onConfirm={cancel}>
              <Button>Cancel</Button>
            </Popconfirm>
          </span>
        ) : (
          <Button onClick={() => edit(record)}>Edit</Button>
        );
      },
    },
  ];

  return (
    <div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center', height: '100vh', backgroundImage: "url('https://via.placeholder.com/800')" }}>
      <Card style={{ width: '80%', opacity: 0.9 }}>
        <Button
          type="primary"
          shape="circle"
          icon={<PlusOutlined />}
          size="large"
          style={{ marginBottom: '20px' }}
          onClick={handleAddUser}
        />
        <Table
          bordered
          dataSource={users}
          columns={columns}
          rowKey="id"
          pagination={{ pageSize: 10 }}
        />
      </Card>
    </div>
  );
};

export default UserList;
