import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Form, Input, Button, message } from 'antd';

const EditUserForm = ({ userId }) => {
  const [form] = Form.useForm();
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchUser = async () => {
      try {
        const response = await axios.get(`http://localhost:3000/users/`);
        form.setFieldsValue(response.data);
      } catch (error) {
        console.error('Error fetching user:', error);
        setError('Error fetching user');
      }
    };

    fetchUser();
  }, [userId, form]);

  const handleSubmit = async (values) => {
    try {
      await axios.put(`http://localhost:3000/users/${userId}`, values);
      message.success('User updated successfully');
    } catch (error) {
      console.error('Error updating user:', error);
      setError('Error updating user');
    }
  };

  return (
    <div>
      <h1>Edit User</h1>
      <Form form={form} onFinish={handleSubmit}>
        <Form.Item name="name" label="Name" rules={[{ required: true, message: 'Please input your name!' }]}>
          <Input placeholder="Name" />
        </Form.Item>
        <Form.Item name="email" label="Email" rules={[{ type: 'email', required: true, message: 'Please input a valid email!' }]}>
          <Input placeholder="Email" />
        </Form.Item>
        <Form.Item name="age" label="Age" rules={[{ type: 'number', required: true, message: 'Please input your age!' }]}>
          <Input placeholder="Age" />
        </Form.Item>
        <Form.Item>
          <Button type="primary" htmlType="submit">
            Update User
          </Button>
        </Form.Item>
      </Form>
      {error && <p>{error}</p>}
    </div>
  );
};

export default EditUserForm;
