import React, { useState, useEffect } from 'react';
import { Table, Input, Popconfirm, Form, Typography, message, Button, Card } from 'antd';
import { PlusOutlined, EditOutlined, DeleteOutlined } from '@ant-design/icons';
import axios from 'axios';

const { Text } = Typography;

const UserList = () => {
  const [users, setUsers] = useState([]);
  const [editingKey, setEditingKey] = useState('');
  const [form] = Form.useForm();
  const [newUserKey, setNewUserKey] = useState(0);

  useEffect(() => {
    fetchData();
  }, []);

  const fetchData = async () => {
    try {
      const response = await axios.get('http://localhost:3000/users');
      setUsers(response.data);
    } catch (error) {
      console.error('Error fetching users:', error);
      message.error('Error fetching users');
    }
  };

  const isEditing = (record) => record.id === editingKey;

  const edit = (record) => {
    setEditingKey(record.id);
    form.setFieldsValue(record);
  };

  const cancel = () => {
    if (editingKey === 'new') {
      const newData = [...users];
      newData.pop();
      setUsers(newData);
      setNewUserKey(newUserKey - 1);
    }
    setEditingKey('');
  };

  const save = async (id) => {
    try {
      const row = await form.validateFields();
      if (id === 'new') {
        await axios.post('http://localhost:3000/users', row);
        message.success('User added successfully');
        fetchData(); // Actualizar la lista despuÃ©s de crear el usuario
      } else {
        await axios.put(`http://localhost:3000/users/${id}`, row);
        message.success('User updated successfully');
      }
      setEditingKey('');
    } catch (error) {
      console.error('Error updating user:', error);
      message.error('Error updating user');
    }
  };

  const handleAddUser = () => {
    const newUser = { id: 'new', name: '', email: '', age: '', key: newUserKey };
    setUsers([...users, newUser]);
    edit(newUser);
    setNewUserKey(newUserKey + 1);
  };

  const columns = [
    {
      title: 'ID',
      dataIndex: 'id',
    },
    {
      title: 'Name',
      dataIndex: 'name',
      editable: true,
      render: (_, record) => {
        const editable = isEditing(record);
        return editable ? (
          <Form.Item name="name" style={{ margin: 0 }}>
            <Input />
          </Form.Item>
        ) : (
          <Text>{record.name}</Text>
        );
      },
    },
    {
      title: 'Email',
      dataIndex: 'email',
      editable: true,
      render: (_, record) => {
        const editable = isEditing(record);
        return editable ? (
          <Form.Item name="email" style={{ margin: 0 }}>
            <Input />
          </Form.Item>
        ) : (
          <Text>{record.email}</Text>
        );
      },
    },
    {
      title: 'Age',
      dataIndex: 'age',
      editable: true,
      render: (_, record) => {
        const editable = isEditing(record);
        return editable ? (
          <Form.Item name="age" style={{ margin: 0 }}>
            <Input />
          </Form.Item>
        ) : (
          <Text>{record.age}</Text>
        );
      },
    },
    {
      title: 'Action',
      dataIndex: 'action',
      render: (_, record) => {
        const editable = isEditing(record);
        return (
          <span>
            {editable ? (
              <>
                <Button type="primary" onClick={() => save(record.id)} style={{ marginRight: 8 }}>
                  {record.id === 'new' ? 'Create' : 'Save'}
                </Button>
                <Popconfirm title="Sure to cancel?" onConfirm={cancel}>
                  <Button>Cancel</Button>
                </Popconfirm>
              </>
            ) : (
              <>
                <Button onClick={() => edit(record)} icon={<EditOutlined />} />
                <Popconfirm title="Sure to delete?" onConfirm={() => handleDelete(record.id)}>
                  <Button icon={<DeleteOutlined />} />
                </Popconfirm>
              </>
            )}
          </span>
        );
      },
    },
  ];

  const handleDelete = async (id) => {
    try {
      await axios.delete(`http://localhost:3000/users/${id}`);
      const newData = users.filter((user) => user.id !== id);
      setUsers(newData);
      message.success('User deleted successfully');
    } catch (error) {
      console.error('Error deleting user:', error);
      message.error('Error deleting user');
    }
  };

  return (
    <div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center', height: '100vh', backgroundImage: "url('https://via.placeholder.com/800')" }}>
      <Card style={{ width: '80%', opacity: 0.9 }}>
        <Button
          type="primary"
          shape="circle"
          icon={<PlusOutlined />}
          size="large"
          style={{ marginBottom: '20px' }}
          onClick={handleAddUser}
        />
        <Table
          bordered
          dataSource={users}
          columns={columns}
          rowKey="key"
          pagination={{ pageSize: 10 }}
        />
      </Card>
    </div>
  );
};

export default UserList;
