import React, { useState, useEffect } from 'react';
import { Table, Input, Popconfirm, Form, Typography, message, Button } from 'antd';
import axios from 'axios';

const { Text } = Typography;

const UserList = () => {
  const [users, setUsers] = useState([]);
  const [editingKey, setEditingKey] = useState('');
  const [form] = Form.useForm();
  const [formData, setFormData] = useState({});

  useEffect(() => {
    fetchData();
  }, []);

  const fetchData = async () => {
    try {
      const response = await axios.get('http://localhost:3000/users');
      setUsers(response.data);
    } catch (error) {
      console.error('Error fetching users:', error);
      message.error('Error fetching users');
    }
  };

  const isEditing = (record) => record.id === editingKey;

  const edit = (record) => {
    setEditingKey(record.id);
    form.setFieldsValue(record);
    setFormData(record);
  };

  const cancel = () => {
    setEditingKey('');
    form.setFieldsValue(formData);
  };

  const save = async (id) => {
    try {
      const row = await form.validateFields();
      const newData = [...users];
      const index = newData.findIndex((item) => id === item.id);

      if (index > -1) {
        const item = newData[index];
        newData.splice(index, 1, { ...item, ...row });
        setUsers(newData);
        setEditingKey('');
        await axios.put(`http://localhost:3000/users/${id}`, row);
        message.success('User updated successfully');
      } else {
        newData.push(row);
        setUsers(newData);
        setEditingKey('');
        await axios.post('http://localhost:3000/users', row);
        message.success('User added successfully');
      }
    } catch (error) {
      console.error('Error updating user:', error);
      message.error('Error updating user');
    }
  };

  const handleAddUser = async () => {
    try {
      const newRow = await form.validateFields();
      await axios.post('http://localhost:3000/users', newRow);
      message.success('User added successfully');
      form.resetFields();
      fetchData();
    } catch (error) {
      console.error('Error adding user:', error);
      message.error('Error adding user');
    }
  };

  const columns = [
    // Columnas existentes...
  ];

  const mergedColumns = columns.map((col) => ({
    ...col,
    onCell: (record) => ({
      record,
      inputType: col.editable ? 'text' : 'none',
      dataIndex: col.dataIndex,
      title: col.title,
      editing: isEditing(record),
    }),
  }));

  return (
    <Form form={form} component={false}>
      <div style={{ marginBottom: 16 }}>
        <Button onClick={handleAddUser} type="primary" style={{ marginBottom: 16 }}>
          Add User
        </Button>
      </div>
      <Table
        bordered
        dataSource={users}
        columns={mergedColumns}
        rowKey="id"
        pagination={{ pageSize: 10 }}
      />
    </Form>
  );
};

export default UserList;
