import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import { Form, Input, Button, message } from 'antd';
import axios from 'axios';

const EditUserForm = () => {
  const { id } = useParams();
  console.log('ID del usuario:', id); // Agrega este log para verificar el ID del usuario
  const [user, setUser] = useState({ name: '', email: '', age: '' });
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchUser = async () => {
      try {
        const response = await axios.get(`http://localhost:3000/users/${id}`);
        setUser(response.data);
      } catch (error) {
        console.error('Error fetching user:', error);
        setError('Error fetching user');
      }
    };

    fetchUser();
  }, [id]);

  const handleChange = e => {
    setUser(prevUser => ({ ...prevUser, [e.target.name]: e.target.value }));
  };

  const handleSubmit = async () => {
    try {
      await axios.put(`http://localhost:3000/users/${id}`, user);
      message.success('User updated successfully');
    } catch (error) {
      console.error('Error updating user:', error);
      setError('Error updating user');
    }
  };

  return (
    <div>
      <h1>Edit User</h1>
      <Form onFinish={handleSubmit}>
        <Form.Item label="Name">
          <Input name="name" value={user.name} onChange={handleChange} placeholder="Name" required />
        </Form.Item>
        <Form.Item label="Email">
          <Input type="email" name="email" value={user.email} onChange={handleChange} placeholder="Email" required />
        </Form.Item>
        <Form.Item label="Age">
          <Input type="number" name="age" value={user.age} onChange={handleChange} placeholder="Age" required />
        </Form.Item>
        <Form.Item>
          <Button type="primary" htmlType="submit">
            Update User
          </Button>
        </Form.Item>
      </Form>
      {error && <p>{error}</p>}
    </div>
  );
};

export default EditUserForm;
