// App.js
import React from 'react';
import axios from 'axios';
import UserList from './components/UserList';
import AddUserForm from './components/AddUserForm';

class App extends React.Component {
  state = {
    users: [],
    error: null
  };

  componentDidMount() {
    this.fetchUsers();
  }

  fetchUsers = () => {
    axios.get('http://localhost:3000/users')
      .then(response => {
        this.setState({ users: response.data });
      })
      .catch(error => {
        this.setState({ error: 'Error al obtener usuarios.' });
      });
  };

  addUser = (user) => {
    axios.post('http://localhost:3000/users', user)
      .then(response => {
        this.setState(prevState => ({
          users: [...prevState.users, response.data]
        }));
      })
      .catch(error => {
        this.setState({ error: 'Error al agregar usuario.' });
      });
  };

  editUser = (userId, updatedUserData) => {
    axios.put(`http://localhost:3000/users/${userId}`, updatedUserData)
      .then(response => {
        this.setState(prevState => ({
          users: prevState.users.map(user => 
            user.id === userId ? response.data : user
          )
        }));
      })
      .catch(error => {
        this.setState({ error: 'Error al editar usuario.' });
      });
  };

  deleteUser = (userId) => {
    axios.delete(`http://localhost:3000/users/${userId}`)
      .then(() => {
        this.setState(prevState => ({
          users: prevState.users.filter(user => user.id !== userId)
        }));
      })
      .catch(error => {
        this.setState({ error: 'Error al eliminar usuario.' });
      });
  };

  render() {
    const { users, error } = this.state;

    return (
      <div>
        <h1>Plataforma de Gesti√≥n de Usuarios</h1>
        {error && <p>{error}</p>}
        <AddUserForm addUser={this.addUser} />
        <UserList 
          users={users} 
          editUser={this.editUser} 
          deleteUser={this.deleteUser} 
        />
      </div>
    );
  }
}

export default App;
