import React from 'react';
import axios from 'axios';
import { Form, Input, Button, message } from 'antd';

class AddUserForm extends React.Component {
  state = {
    name: '',
    email: '',
    age: ''
  };

  handleChange = e => {
    this.setState({ [e.target.name]: e.target.value });
  };

  handleSubmit = e => {
    e.preventDefault();
    const { name, email, age } = this.state;
    axios.post('http://localhost:3000/users', {id,  name, email, age })
      .then(response => {
        console.log('User added successfully');
        message.success('User added successfully');
      })
      .catch(error => {
        console.error('Error adding user:', error);
        message.error('Error adding user');
      });
  };

  render() {
    const { name, email, age } = this.state;

    return (
      <div>
        <h1>Add User</h1>
        <Form layout="vertical" onSubmit={this.handleSubmit}>
          <Form.Item label="Name">
            <Input type="text" name="name" value={name} onChange={this.handleChange} placeholder="Name" required />
          </Form.Item>
          <Form.Item label="Email">
            <Input type="email" name="email" value={email} onChange={this.handleChange} placeholder="Email" required />
          </Form.Item>
          <Form.Item label="Age">
            <Input type="number" name="age" value={age} onChange={this.handleChange} placeholder="Age" required />
          </Form.Item>
          <Form.Item>
            <Button type="primary" htmlType="submit">Add User</Button>
          </Form.Item>
        </Form>
      </div>
    );
  }
}

export default AddUserForm;
