import React, { useState, useEffect } from 'react';
import { Table, Input, Popconfirm, Form, Typography, message, Button } from 'antd';
import { PlusOutlined } from '@ant-design/icons';
import axios from 'axios';

const { Text } = Typography;

const UserList = () => {
  const [users, setUsers] = useState([]);
  const [editingKey, setEditingKey] = useState('');
  const [form] = Form.useForm();
  const [formData, setFormData] = useState({});

  useEffect(() => {
    fetchData();
  }, []);

  const fetchData = async () => {
    try {
      const response = await axios.get('http://localhost:3000/users');
      setUsers(response.data);
    } catch (error) {
      console.error('Error fetching users:', error);
      message.error('Error fetching users');
    }
  };

  const isEditing = (record) => record.id === editingKey;

  const edit = (record) => {
    setEditingKey(record.id);
    form.setFieldsValue(record);
    setFormData(record);
  };

  const cancel = () => {
    setEditingKey('');
    form.setFieldsValue(formData);
  };

  const save = async (id) => {
    try {
      const row = await form.validateFields();
      const newData = [...users];
      const index = newData.findIndex((item) => id === item.id);

      if (index > -1) {
        const item = newData[index];
        newData.splice(index, 1, { ...item, ...row });
        setUsers(newData);
        setEditingKey('');
        await axios.put(`http://localhost:3000/users/${id}`, row);
        message.success('User updated successfully');
      } else {
        newData.push(row);
        setUsers(newData);
        setEditingKey('');
        await axios.post('http://localhost:3000/users', row);
        message.success('User added successfully');
      }
    } catch (error) {
      console.error('Error updating user:', error);
      message.error('Error updating user');
    }
  };

  const handleAddUser = () => {
    const newUser = { id: '', name: '', email: '', age: '' };
    setUsers([...users, newUser]);
    edit(newUser);
  };

  const columns = [
    {
      title: 'ID',
      dataIndex: 'id',
    },
    {
      title: 'Name',
      dataIndex: 'name',
      editable: true,
      render: (_, record) => {
        const editable = isEditing(record);
        return editable ? (
          <Form.Item name="name" style={{ margin: 0 }}>
            <Input />
          </Form.Item>
        ) : (
          <Text>{record.name}</Text>
        );
      },
    },
    {
      title: 'Email',
      dataIndex: 'email',
      editable: true,
      render: (_, record) => {
        const editable = isEditing(record);
        return editable ? (
          <Form.Item name="email" style={{ margin: 0 }}>
            <Input />
          </Form.Item>
        ) : (
          <Text>{record.email}</Text>
        );
      },
    },
    {
      title: 'Age',
      dataIndex: 'age',
      editable: true,
      render: (_, record) => {
        const editable = isEditing(record);
        return editable ? (
          <Form.Item name="age" style={{ margin: 0 }}>
            <Input />
          </Form.Item>
        ) : (
          <Text>{record.age}</Text>
        );
      },
    },
    {
      title: 'Action',
      dataIndex: 'action',
      render: (_, record) => {
        const editable = isEditing(record);
        return editable ? (
          <span>
            <Button type="primary" onClick={() => save(record.id)} style={{ marginRight: 8 }}>
              Save
            </Button>
            <Popconfirm title="Sure to cancel?" onConfirm={cancel}>
              <Button>Cancel</Button>
            </Popconfirm>
          </span>
        ) : (
          <Button onClick={() => edit(record)}>Edit</Button>
        );
      },
    },
  ];

  return (
    <>
      <Table
        bordered
        dataSource={users}
        columns={columns}
        rowKey="id"
        pagination={{ pageSize: 10 }}
      />
      <Button
        type="primary"
        shape="circle"
        icon={<PlusOutlined />}
        size="large"
        style={{ position: 'absolute', right: 16, bottom: 16 }}
        onClick={handleAddUser}
      />
    </>
  );
};

export default UserList;
