import React, { useState, useEffect } from 'react';
import { Table, Input, Popconfirm, Form, Typography, message, Button, Card } from 'antd';
import { PlusOutlined, DeleteOutlined } from '@ant-design/icons';
import axios from 'axios';

const { Text } = Typography;

const UserList = () => {
  const [users, setUsers] = useState([]);
  const [editingKey, setEditingKey] = useState('');
  const [form] = Form.useForm();
  const [newUserKey, setNewUserKey] = useState(0);
  const [showCreateButton, setShowCreateButton] = useState(false); // Nuevo estado para mostrar el botón de crear usuario en la fila nueva

  useEffect(() => {
    fetchData();
  }, []);

  const fetchData = async () => {
    try {
      const response = await axios.get('http://localhost:3000/users');
      setUsers(response.data);
    } catch (error) {
      console.error('Error fetching users:', error);
      message.error('Error fetching users');
    }
  };

  const isEditing = (record) => record.id === editingKey;

  const edit = (record) => {
    setEditingKey(record.id);
    form.setFieldsValue(record);
  };

  const cancel = () => {
    if (editingKey === 'new') {
      const newData = users.slice(0, users.length - 1);
      setUsers(newData);
      setShowCreateButton(false); // Ocultar el botón de crear usuario en la fila nueva al cancelar
    }
    setEditingKey('');
  };

  const save = async (id) => {
    try {
      const row = await form.validateFields();
      const newData = [...users];
      const index = newData.findIndex((item) => id === item.id);

      if (index > -1) {
        const item = newData[index];
        newData.splice(index, 1, { ...item, ...row });
        setUsers(newData);
        setEditingKey('');
        await axios.put(`http://localhost:3000/users/${id}`, row);
        message.success('User updated successfully');
      } else {
        const newUser = { ...row, id: `temp-${newUserKey}` };
        setUsers([...newData, newUser]);
        setEditingKey('');
        setNewUserKey(newUserKey + 1);
        await axios.post('http://localhost:3000/users', newUser);
        message.success('User added successfully');
      }
      setShowCreateButton(false); // Ocultar el botón de crear usuario en la fila nueva después de guardar
    } catch (error) {
      console.error('Error updating user:', error);
      message.error('Error updating user');
    }
  };

  const handleAddUser = () => {
    const newUser = { id: 'new', name: '', email: '', age: '', key: newUserKey };
    setUsers([...users, newUser]);
    edit(newUser);
    setNewUserKey(newUserKey + 1);
    setShowCreateButton(true); // Mostrar el botón de crear usuario en la fila nueva
  };

  const handleCreateUser = async () => {
    try {
      const row = await form.validateFields();
      const newUser = { ...row, id: `temp-${newUserKey}` };
      setUsers([...users.filter(user => user.id !== 'new'), newUser]);
      setEditingKey('');
      setNewUserKey(newUserKey + 1);
      await axios.post('http://localhost:3000/users', newUser);
      message.success('User created successfully');
      setShowCreateButton(false); // Ocultar el botón de crear usuario en la fila nueva después de crear el usuario
    } catch (error) {
      console.error('Error creating user:', error);
      message.error('Error creating user');
    }
  };

  const handleDeleteUser = async (id) => {
    try {
      await axios.delete(`http://localhost:3000/users/${id}`);
      setUsers(users.filter(user => user.id !== id));
      message.success('User deleted successfully');
    } catch (error) {
      console.error('Error deleting user:', error);
      message.error('Error deleting user');
    }
  };

  const columns = [
    {
      title: 'ID',
      dataIndex: 'id',
    },
    {
      title: 'Name',
      dataIndex: 'name',
      editable: true,
      render: (_, record) => {
        const editable = isEditing(record);
        return editable ? (
          <Form.Item name="name" style={{ margin: 0 }}>
            <Input />
          </Form.Item>
        ) : (
          <Text>{record.name}</Text>
        );
      },
    },
    {
      title: 'Email',
      dataIndex: 'email',
      editable: true,
      render: (_, record) => {
        const editable = isEditing(record);
        return editable ? (
          <Form.Item name="email" style={{ margin: 0 }}>
            <Input />
          </Form.Item>
        ) : (
          <Text>{record.email}</Text>
        );
      },
    },
    {
      title: 'Age',
      dataIndex: 'age',
      editable: true,
      render: (_, record) => {
        const editable = isEditing(record);
        return editable ? (
          <Form.Item name="age" style={{ margin: 0 }}>
            <Input />
          </Form.Item>
        ) : (
          <Text>{record.age}</Text>
        );
      },
    },
    {
      title: 'Action',
      dataIndex: 'action',
      render: (_, record) => {
        const editable = isEditing(record);
        return editable ? (
          <span>
            <Button type="primary" onClick={() => save(record.id)} style={{ marginRight: 8 }}>
              Save
            </Button>
            <Popconfirm title="Sure to cancel?" onConfirm={cancel}>
              <Button>Cancel</Button>
            </Popconfirm>
          </span>
        ) : (
          <>
            {record.id === 'new' ? (
              <>
                <Button type="primary" onClick={handleCreateUser}>
                  Create User
                </Button>
                <Button onClick={cancel}>Cancel</Button>
              </>
            ) : (
              <>
                <Button onClick={() => edit(record)}>Edit</Button>
                <Popconfirm title="Sure to delete?" onConfirm={() => handleDeleteUser(record.id)}>
                  <Button type="danger" icon={<DeleteOutlined />} />
                </Popconfirm>
              </>
            )}
          </>
        );
      },
    },
  ];

  return (
    <div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center', height: '100vh', backgroundImage: "url('https://via.placeholder.com/800')" }}>
      <Card style={{ width: '80%', opacity: 0.9 }}>
        <Button
          type="primary"
          shape="circle"
          icon={<PlusOutlined />}
          size="large"
          style={{ marginBottom: '20px' }}
          onClick={handleAddUser}
        />
        <Table
          bordered
          dataSource={users}
          columns={columns}
          rowKey="key"
          pagination={{ pageSize: 10 }}
        />
      </Card>
    </div>
  );
};

export default UserList;
